-- ----------------------------
-- Function structure for find_all_user_records
-- ----------------------------
DROP FUNCTION IF EXISTS "public"."find_all_user_records"("user_id_val" int4);
CREATE OR REPLACE FUNCTION "public"."find_all_user_records"("user_id_val" int4)
  RETURNS TABLE("table_name" text, "record_count" int8, "sample_data" jsonb) AS $BODY$
DECLARE
    rec RECORD;
BEGIN
    -- 创建临时表
    CREATE TEMP TABLE temp_results (
        table_name TEXT,
        record_count BIGINT,
        sample_data JSONB
    );
    
    -- 遍历所有包含 user_id 列的表
    FOR rec IN
        SELECT table_name::TEXT
        FROM information_schema.columns
        WHERE column_name = 'user_id'
          AND table_schema = 'public'  -- 替换为您的schema
    LOOP
        EXECUTE format(
            'INSERT INTO temp_results '
            'SELECT %L, '
            '(SELECT COUNT(*) FROM %I WHERE user_id = $1), '
            '(SELECT to_jsonb(t) FROM %I t WHERE user_id = $1 LIMIT 1)',
            rec.table_name,
            rec.table_name,
            rec.table_name
        ) USING user_id_val;
    END LOOP;
    
    -- 返回结果
    RETURN QUERY SELECT * FROM temp_results;
    
    -- 清理
    DROP TABLE temp_results;
END;
$BODY$
  LANGUAGE plpgsql VOLATILE
  COST 100
  ROWS 1000;

